using System;
using System.Collections.Generic;
using System.Text;
using System.IO;

namespace OpenBridgeApp.Palettes 
{
    public enum ObWeightType
    {
        Bold = 0,
        Regular,
        Semibold
    }

	public class ObFontClass 
    {

		public double LineHeight;
		public double Size;
		public ObWeightType Weight;

		public ObFontClass()
        {
            LineHeight = 32;
            Size = 12;
            Weight = ObWeightType.Regular;
        }

		~ObFontClass()
        {
		}

        public static ObFontClass CreateFont(double lineHeight, double size, ObWeightType weight)
        {
            ObFontClass newfont = new ObFontClass();
            newfont.LineHeight = lineHeight;
            newfont.Size = size;
            newfont.Weight = weight;
            return newfont;
        }

	}//end ObFontClass

}//end namespace Palettes
